    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       if(newName.getText().equals("") || newLocation.getText().equals("")){
           JOptionPane.showMessageDialog(null,"Any field most by empty","Message",JOptionPane.WARNING_MESSAGE); 
       }
       else{
            String typeC = (String) ClassroomType.getSelectedItem();

            String name = newName.getText();
            String number = (String) jSpinner1.getValue().toString();
            String location = newLocation.getText();
            String capacity = (String) newCapacity.getValue().toString();
            if(typeC.equals("Practical")){
                 String type = (String) NewAux1.getSelectedItem();
                 String amountequipment = (String) NewAux2.getSelectedItem();
                 ClassroomPractical classroomPractical = new ClassroomPractical(amountequipment, type, name, number, location, capacity);
                 classroomPractical.insert();
            }
            else{
                 String AC = (String) NewAux1.getSelectedItem();
                 String ME = (String) NewAux2.getSelectedItem();
                 ClassroomTheoretical classroomTheoretical = new ClassroomTheoretical(AC, ME, name, number, location, capacity);
                 classroomTheoretical.insert();
            }
            this.dispose();
            main.coordinatorW.setVisible(true);
            newName.setText("");
            newLocation.setText("");
       }
     
    }//GEN-LAST:event_jButton1ActionPerformed

