    private void ClassroomTypeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ClassroomTypeActionPerformed
        // TODO add your handling code here:
        String type = (String) ClassroomType.getSelectedItem();
        
        if(type.equals("Practical")){
            Aux1.setText("Type");
            NewAux1.removeAllItems();
            NewAux1.addItem("Computer lab");
            NewAux1.addItem("Cientific lab");
            aux2.setText("Amount of equipment");
            NewAux2.removeAllItems();
            int x =15;
            while(x<=30){
                NewAux2.addItem(String.valueOf(x));
                x++;
            }
        }
        else{
            Aux1.setText("A/C");
            NewAux1.removeAllItems();
            NewAux1.addItem("Yes");
            NewAux1.addItem("no");
            aux2.setText("Multimedia equipment");
            NewAux2.removeAllItems();
            NewAux2.addItem("Yes");
            NewAux2.addItem("No");
        }
    }//GEN-LAST:event_ClassroomTypeActionPerformed

