    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(newname.getText().equals("")){
             JOptionPane.showMessageDialog(null,"Any field most by empty","Message",JOptionPane.WARNING_MESSAGE);
        }
        else{
            String name = newname.getText();
            String credits = newCredits.getValue().toString();
            String sem = semester.getSelectedItem().toString();
            String mat = material.getSelectedItem().toString();
            String sys = System.getSelectedItem().toString();

            PracticalSubject Ps = new PracticalSubject(mat,sys, name, credits, sem);
            Ps.insert();

            JOptionPane.showMessageDialog(null, "Complete Process", "Message", JOptionPane.OK_CANCEL_OPTION);
            this.dispose();
            main.coordinatorW.setVisible(true);
            newname.setText("");
            newCredits.setValue(0);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

