    private void materialActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_materialActionPerformed
        // TODO add your handling code here:
        String type = material.getSelectedItem().toString();
        switch (type) {
            case "Computer":
                System.removeAllItems();
                System.addItem("Windows");
                System.addItem("Linux");
                System.addItem("iOS");
                break;
            case "Tablets":
                System.removeAllItems();
                System.addItem("Android");
                System.addItem("Windows phone");
                System.addItem("iOS");
                break;
        }
    }//GEN-LAST:event_materialActionPerformed

